// Helper functions
export function getWorkingStores() {
  return SHOPIFY_STORES.filter(store => store.url.includes('.json'));
}

export function getStoreByName(name) {
  return SHOPIFY_STORES.find(store => 
    store.name.toLowerCase().includes(name.toLowerCase())
  );
}

export function getTotalStoreCount() {
  return SHOPIFY_STORES.length;
}

